  `genericID` int(11) NOT NULL,
  `format` enum('ampule','inhaler','capsule','solution','nebulizer','sachet','syrup','syrup dry','spray','supository','tablet','drops','tube','vial','powder') COLLATE utf8_bin NOT NULL,
  `size` decimal(10,2) DEFAULT NULL,
