DROP TABLE IF EXISTS `drugDispense`;

CREATE TABLE `drugDispense` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `drugID` int(11) NOT NULL,
  `numberTot` int(11) DEFAULT NULL,
  `date` date NOT NULL,
